        oidcClaimsMap.put("name", "name");
        oidcClaimsMap.put("email", "email");
        oidcClaimsMap.put("phone", "phone");
        oidcClaimsMap.put("gender", "gender");
        oidcClaimsMap.put("dateOfBirth", "birthdate");
